import React, {useState, useEffect} from "react";
import {Link, Redirect} from "react-router-dom"
import {connect} from"react-redux"


import logo from "../../assets/images/logos/logo-complet.png"
import menuIcon from "../../assets/icons/menu-icon.svg"
import adminIcon from "../../assets/icons/admin-icon.svg"
import profileIcon from "../../assets/icons/profile-icon.svg"
import cartIcon from "../../assets/icons/cart-icon.svg"
import searchIcon from "../../assets/icons/search-icon.svg"

import RegisterPopUp from "../../components/registerPopUp"

import { Button } from '@material-ui/core';


const Header = (props) => {

    const [openRegisterPopUp, setOpenRegisterPopUp] = useState(false);
    const [redirect, setRedirect] = useState(false)

    const registerForm = () => {
        setOpenRegisterPopUp(false)
        setRedirect(true)
    }

    const closeRegisterForm = () => {
        setOpenRegisterPopUp(false)

    }

    return (<div className="header-container default-background-gradient">
                <div className="header-nav-container">
                    <img className="header-nav-menu-icon" src={menuIcon} alt="Icone menu"/>
                    {/*<nav className="header-nav">
                        <img className="header-nav-icon" src={adminIcon} alt="Panneau d'administration"/>
                        <img className="header-nav-icon" src={profileIcon} alt="Profil"/>
                        <img className="header-nav-icon" src={cartIcon} alt="Panier"/>
                        <img className="header-nav-icon" src={searchIcon} alt="Rechercher"/>
</nav>*/}
                </div>
                <div className="login-register">
                    <Button color="secondary" variant="outlined">Se connecter</Button>
                    <Button color="secondary" variant="outlined" onClick={(e) => {setOpenRegisterPopUp(true)}}>S'enregistrer</Button>
                    {openRegisterPopUp && <RegisterPopUp registerForm={(e) => { registerForm()}} opened={openRegisterPopUp} closeRegisterForm={(e) => {closeRegisterForm()}}/>}
                </div>
                <div>

                </div>
                <div className="headerhome-content-container">
                    <div className="headerhome-content">
                        <img className="header-logo" src={logo} alt="Logo"/>
                    </div>
                </div>
                <div className="header-buttons-container">
                    <Button color="primary" variant="contained">Boutique</Button>
                    <Button color="secondary" variant="outlined">Etal du jour</Button>
                </div>

            </div>)
}

const mapStateToProps = (store) => {
    user : store.user
}

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(Header)
