import axios from "axios"
import {config} from "../config"

export const saveUser = (datas) => {

    return axios.post(config.api_url + "/user/register", datas)
    .then(response => {
        return response
    })
    .catch(err => {
        return err
    })
}

export const loginUser = (datas) => {

    return axios.post(config.api_url + "/user/login", datas)
    .then(response => {
        return response
    })
    .catch(err => {
        return err
    })
}

export const getAllUsers = async () => {
    response = await axios.get(config.api_url + "/user/all")

    return axios.get(config.api_url + "/user/all")
    .then(response => {
        return response
    })
    .catch(err => {
        console.log(err)
    })
}
