import React, {useState, useEffect} from 'react'
import {loginUser} from "../api/user"

import tokenConfig from "../tokenConfig"

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';

const LoginPopUp = (props) => {

    //Définition des variables
    const [email, setEmail] = useState("")
    const [emailError, setEmailError] = useState("")
    const [emailErrorText, setEmailErrorText] = useState("")

    const [password, setPassword] = useState("")
    const [passwordError, setPasswordError] = useState(false)
    const [passwordErrorText, setPasswordErrorText] = useState("")

    // RegEx de vérification du mail
    let emailRegEx = /^[a-z]+([\.\-_]?[a-z0-9]+)*@([a-z]{3,})+(\.[a-z]{2,3})+$/

    const globalVerify = () => {

            if(!email){
                setEmailError(true)
                setEmailErrorText("Adresse email non renseignée")
            }
            else {
                if(!email.match(emailRegEx)){
                    setEmailError(true)
                    setEmailErrorText("Adresse email non valide")
                }
                else {
                    setEmailError(false)
                    setEmailErrorText("")
                }
            }
            if(!password){
                setPasswordError(true)
                setPasswordErrorText("Mot de passe non reseigné")
            }
            else {
                setPasswordError(false)
                setPasswordErrorText("")
            }

            if(email.match(emailRegEx)){
                let datas = {
                    email: email,
                    password: password
                }    

                loginUser(datas)
                .then(response => {
                    if(response.data.status === 404){
                        setEmailErrorText("Utilisateur inexistant")
                        setEmailError(true)
                    } else {
                        setEmailErrorText("")
                        setEmailError(false)

                        if(response.data.status === 401) {
                            setPasswordErrorText("Mot de passe incorrect")
                            setPasswordError(true)
                        }
                        else {
                            localStorage.setItem(tokenConfig.to)
                            //props.closeLoginForm()
                        }
                    }
                })
            }

    }

    const onSubmit = () =>{
        globalVerify() 
    }

    return(
        <Dialog
            open={props.opened}
            onClose={(e) => {props.closeLoginForm()}}
        >
            <DialogTitle>Se connecter</DialogTitle>
            <DialogContent>
            <form className="login-register-form" autoComplete="off">
                <FormControl error>
                    <TextField label="Email" color="primary" className="register-form-input" required onChange={(e) => {setEmail(e.target.value)}} error={emailError} type="email" value={email}/>
                    <FormHelperText>{emailErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    <TextField label="Mot de passe" color="primary" className="register-form-input" required onChange={(e) => {setPassword(e.target.value)}} error={passwordError} type="password" value={password}/>
                    <FormHelperText>{passwordErrorText}</FormHelperText>
                </FormControl>
            </form>
            </DialogContent>
            <DialogActions>
            <Button onClick={(e) => {props.closeLoginForm()}} color="primary">
                Annuler
            </Button>
            <Button onClick={(e) => {onSubmit()}} color="primary">
                Envoyer
            </Button>
            </DialogActions>
        </Dialog>
    )
}

export default LoginPopUp
