import React, {useState, useEffect} from "react";
import {connect} from "react-redux"
import {loadAllUsers} from "../../actions/users/usersAction"
import {getAllUsers} from "../../api/user"
import {Link, Redirect} from "react-router-dom"

import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

import AdminMenu from "../../components/adminMenu"
import DeletePopUp from "../../components/deletePopUp";

import editIcon from "../../assets/icons/edit-blue.svg"
import deleteIcon from "../../assets/icons/delete-blue.svg"

const AdminUsers = (props) => {
    const [headerTitle, setHeaderTitle] = useState("Administration")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Administration", link:"/admin"},
                                                                {value: "Utilisateurs", link:null}])

    const [range, setRange] = useState(3)
    const [startPoint, setStartPoint] = useState(0)
    const [page, setPage] = useState(1)

    const [openDeletePopUp, setOpenDeletePopUp] = useState(false);
    const [redirect, setRedirect] = useState(false)


    useEffect(() => {
        getAllUsers()
        .then(response => {
            props.loadAllUsers(response.data.users)
        })

    }, [])

    const closeDeleteForm = () => {
        setOpenDeletePopUp(false)
        setRedirect(true)
    }

    const showUsers = () => {
        let users = props.users.users
        let items = []

        if(users.length - (startPoint + range) < range){
            if(page > 1){
                startPoint = (page-1)*range
            }
            if(users.length < range){
                for(let i = 0; i < users.length; i++){
                    items.push(
                        <div key={i} className="users-list-item">
                            <div className="user-item-name">{users[i].first_name} {users[i].last_name}</div>
                            <div className="edit-delete-user-container">
                                <Link to={"/admin/users/edit/"+users[i].id}><img src={editIcon} alt="Edit icon" className="edit-delete-user"/></Link>
                                {<Link to={"/admin/users/delete/"+users[i].id}><img src={deleteIcon} alt="Delete icon" className="edit-delete-user"/></Link>}
                                {/*<img src={deleteIcon} alt="Delete icon" className="edit-delete-user" onClick={(e) => {setOpenDeletePopUp(true)}}/>
                                {openDeletePopUp && <DeletePopUp closeDeleteForm={(e) => {closeDeleteForm()}} userName={users[i].first_name + " " + users[i].last_name} userId={users[i].id}opened={openDeletePopUp} />}*/}

                            </div>
                        </div>
                    )
                }
            }
            else {
                for(let i = startPoint; i < startPoint+range; i++){
                    items.push(
                        <div key={i} className="users-list-item">
                            <div className="user-item-name">{users[i].first_name} {users[i].last_name}</div>
                            <div className="edit-delete-user-container">
                                <Link to={"/admin/users/edit/"+users[i].id}><img src={editIcon} alt="Edit icon" className="edit-delete-user"/></Link>
                                {<Link to={"/admin/users/delete/"+users[i].id}><img src={deleteIcon} alt="Delete icon" className="edit-delete-user"/></Link>}
                                {/*<img src={deleteIcon} alt="Delete icon" className="edit-delete-user" onClick={(e) => {setOpenDeletePopUp(true)}}/>
                                {openDeletePopUp && <DeletePopUp closeDeleteForm={(e) => {closeDeleteForm()}} userName={users[i].first_name + " " + users[i].last_name} userId={users[i].id}opened={openDeletePopUp} />}*/}

                            </div>
                        </div>
                    )
                }
            }
        }

        return(
            <div className="users-list">
                {items}
            </div>

        )
    }

    const showPagesList = () => {
        let pagesList = []
        let users = props.users.users
        let numberOfPages = users.length % range

        for(let i = 1; i < numberOfPages; i ++) {
            console.log("page ", i)
        }

    }
    return(
        <div className="root">
                <Header />
                <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
                <div className="admin-container">
                    <AdminMenu />

                    <div className="admin-content">
                        <h4>Gestion des utilisateurs</h4>
                        <div className="users-list-container">
                            {showUsers()}
                        </div>
                        <div className="admin-page-selection">
                            {showPagesList()}
                        </div>
                    </div>
                </div>
                <Footer />
        </div>
    )
}

const mapStateToProps = (store) => {
    return {
        user : store.user,
        users: store.users
    }
}

const mapDispatchToProps = {
    loadAllUsers
}

export default connect(mapStateToProps, mapDispatchToProps)(AdminUsers)
