import React, {useState, useEffect} from 'react'
import MaskedInput from 'react-text-mask';

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import { withStyles, makeStyles } from "@material-ui/styles";
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';

const RegisterPopUp = (props) => {

    //Définition des variables
    const [firstName, setFirstName] = useState("")
    const [firstNameError, setFirstNameError] = useState(false)
    const [firstNameErrorText, setFirstNameErrorText] = useState("")

    const [lastName, setLastName] = useState("")
    const [lastNameError, setLastNameError] = useState(false)
    const [lastNameErrorText, setLastNameErrorText] = useState("")

    const [email, setEmail] = useState("")
    const [emailError, setEmailError] = useState("")
    const [emailErrorText, setEmailErrorText] = useState("")

    const [password, setPassword] = useState("")
    const [passwordError, setPasswordError] = useState(false)
    const [passwordErrorText, setPasswordErrorText] = useState("")

    const [phone, setPhone] = useState("")
    const [phoneError, setPhoneError] = useState(false)
    const [phoneErrorText, setPhoneErrorText] = useState("")
    const [autoFocusPhone, setAutoFocusPhone] = useState(true)

    const [address, setAddress] = useState("")
    const [zip, setZip] = useState("")
    const [city, setCity] = useState("")
    const [redirect, setRedirect] = useState("")
    const [verifyAll, setVerifyAll] = useState(false)



    // RegEx de vérification du mail
    let mailRegEx = /^[a-z]+([\.\-_]?[a-z0-9]+)*@([a-z]{3,})+(\.[a-z]{2,3})+$/

    // RegEx de vérification du Téléphone
    let phoneRegEx = /^(?:(?:\+|00)33|0)\s*[1-9](?:[\s.-]*\d{2}){4}$/

    //Fonctions de vérifications du formulaire
    const verifyFirstName = () => {
        if(!firstName){
            setFirstNameError(true)
            setFirstNameErrorText("Veuillez saisir un prénom")
        }
        else {
            setFirstNameError(false)
            setFirstNameErrorText("")
        }
    }

    const verifyLastName = () => {
        if(!lastName){
            setLastNameError(true)
            setLastNameErrorText("Veuillez saisir un nom")
        }
        else {
            setLastNameError(false)
            setLastNameErrorText("")
        }
    }

    const verifyMail = () => {
        if(!email){
            setEmailError(true)
            setEmailErrorText("Adresse email non renseignée")
        }
        else {
            if(!email.match(mailRegEx)){
                setEmailError(true)
                setEmailErrorText("Adresse email non valide")
            }
            else {
                setEmailError(false)
                setEmailErrorText("")
            }
        }

    } 

    const verifyPhone = async () => {
        if(!phone){
            setPhoneError(true)
            setPhoneErrorText("N° de téléphone non renseigné")
        }
        else{
            if(!phone.match(phoneRegEx)){
                setPhoneError(true)
                setPhoneErrorText("N° de téléphone incorrect")
            }
            else {
                setPhoneError(false)
                setPhoneErrorText("")
            }
        }
    }

    const globalVerify = () => {
        /*//if(firstNameError || lastNameError || emailError || phoneError){
            //if(!firstNameError && !lastNameError && !emailError && !phoneError){    
                setVerifyAll(true)    
            console.log(verifyAll)

            }
            else {
                setVerifyAll(false)
                console.log(verifyAll)
            }*/
            if(!firstName){
                setFirstNameError(true)
                setFirstNameErrorText("Veuillez saisir un prénom")
            }
            else {
                setFirstNameError(false)
                setFirstNameErrorText("")
            }

            if(!lastName){
                setLastNameError(true)
                setLastNameErrorText("Veuillez saisir un nom")
            }
            else {
                setLastNameError(false)
                setLastNameErrorText("")
            }
            if(!email){
                setEmailError(true)
                setEmailErrorText("Adresse email non renseignée")
            }
            else {
                if(!email.match(mailRegEx)){
                    setEmailError(true)
                    setEmailErrorText("Adresse email non valide")
                }
                else {
                    setEmailError(false)
                    setEmailErrorText("")
                }
            }
            if(!phone){
                setPhoneError(true)
                setPhoneErrorText("N° de téléphone non renseigné")
            }
            else{
                if(!phone.match(phoneRegEx)){
                    setPhoneError(true)
                    setPhoneErrorText("N° de téléphone incorrect")
                }
                else {
                    setPhoneError(false)
                    setPhoneErrorText("")
                }
            }
            if(firstNameError || lastNameError || emailError || phoneError){
                console.log("erreur")
            }
            else {
                console.log("ok")
            }
    }

    const onSubmit = () =>{
        verifyFirstName()
        verifyLastName()
        verifyMail()
        verifyPhone()
        globalVerify()
    }

    //Fonction de création du masque pour le numéro de téléphone
    function PhoneNumberMask(props) {
        const { inputRef, ...other } = props;

        return (
          <MaskedInput
            {...other}
            value={phone}
            mask={[/[0-9]/, /\d/,".", /\d/, /\d/,".", /\d/, /\d/,".",/\d/, /\d/,".",/\d/, /\d/]}
            placeholderChar={'\u2000'}

          />
        );
      }


    return(
        <Dialog
            open={props.opened}
            onClose={(e) => {props.closeRegisterForm()}}
        >
            <DialogTitle>S'enregistrer</DialogTitle>
            <DialogContent>
            <form className="register-form" autoComplete="off">
                <FormControl error>
                    <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={lastNameError} />
                    <FormHelperText>{lastNameErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    <TextField label="Prénom" color="primary" className="register-form-input" required onChange={(e) => {setFirstName(e.target.value)
                    console.log(firstName.length)}} error={firstNameError} value={firstName}/>
                    <FormHelperText>{firstNameErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    <TextField label="Email" color="primary" className="register-form-input" required onChange={(e) => {setEmail(e.target.value)}} error={emailError} type="email" value={email}/>
                    <FormHelperText>{emailErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    {/*<TextField label="Téléphone" color="primary" className="register-form-input" required onChange={(e) => {setPhone(e.target.value)}} error={phoneError} type="tel"/>
                    <TextField InputProps={{ inputComponent: PhoneNumberMask }} label="Téléphone" color="primary" className="register-form-input" required  error={phoneError} type="tel"  onChange={setPhoneNumber} autoFocus={autoFocusPhone}/>*/}

                    <TextField label="Téléphone" color="primary" className="register-form-input" required  error={phoneError} type="tel"  onChange={(e) => {setPhone(e.target.value)}} />

                    <FormHelperText>{phoneErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    <TextField label="Adresse" color="primary" className="register-form-input" onChange={(e) => {setAddress(e.target.value)}} />
                </FormControl>

                <FormControl error>
                    <TextField label="Code Postal" color="primary" className="register-form-input" onChange={(e) => {setZip(e.target.value)}} />
                </FormControl>

                <FormControl error>
                    <TextField label="Ville" color="primary" className="register-form-input" onChange={(e) => {setCity(e.target.value)}} />
                </FormControl>
            </form>
            </DialogContent>
            <DialogActions>
            <Button onClick={(e) => {props.closeRegisterForm()}} color="primary">
                Annuler
            </Button>
            <Button onClick={(e) => {onSubmit()}} color="primary" autoFocus>
                Envoyer
            </Button>
            </DialogActions>
        </Dialog>
    )
}

export default RegisterPopUp
