import axios from "axios"
import {config} from "../config"
import tokenConfig from "../tokenConfig"
const token = localStorage.getItem(tokenConfig.token.name)


export const getFishesCount = () => {

    return axios.get(config.api_url+"/product/allfishes/count")
    .then(response => {
        return response.data.result[0].fishes_count;
    })
    .catch(err => { console.log(err)})
}

export const getCrustaceansCount = () => {
    return axios.get(config.api_url+"/product/allcrustaceans/count")
    .then(response => {
        return response.data.result[0].crustaceans_count
    })
    .catch(err => { console.log(err)})
}

export const getAllProducts = () => {
    return axios.get(config.api_url+"/product/all")
    .then(response => {
        return response.data.result
    })
    .catch(err => { console.log(err)})
}

export const saveProduct = (datas) => {
    return axios.post(config.api_url + "/product/save", datas, {headers: {"x-access-token": token}})
    .then(response => {
        return response
    })
    .catch(err => {console.log(err)})
}

export const saveProductPict = (fileDatas, datas) => {
    let formData = new FormData()
    formData.append('image', fileDatas)

    return axios({method: "post",
                  url: config.api + "/product/pict",
                  data: formData,
                  headers: {
                      "Content-Type":"multipart/form-data",
                      "x-access-token":token
                  }})
                .then(response => {return response})
                .catch(err => {console.log(err)})
}



export const getAllCategories = () => {
    return axios.get(config.api + "/product/categories/all", {headers: {"x-access-token":token}})
}
