import React, {useState, useEffect} from "react"
import {saveProduct, getAllProductCat} from "../../api/products"

import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

import AdminMenu from "../../components/adminMenu"

import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputAdornment from '@material-ui/core/InputAdornment';

import {makeStyles } from "@material-ui/styles";

const AdminProductAdd = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Administration")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Administration", link:"/admin"},
                                                                {value: "Produits", link:"/admin/products"},
                                                                {value: "Ajout", link:null}])

    const [productName, setProductName] = useState("")
    const [productNameError, setProductNameError] = useState(false)
    const [productNameErrorMsg, setProductNameErrorMsg] = useState("")

    const [productPrice, setProductPrice] = useState("")
    const [productPriceError, setProductPriceError] = useState(false)
    const [productPriceErrorMsg, setProductPriceErrorMsg] = useState("")

    const [productDesc, setProductDesc] = useState("")

    const [categories, setCategories] = useState([])
    const [selectedCategory, setSelectedCategory] = useState(0)

    const [selectedFile, setSelectedFile] = useState("")
    const [productPict, setProductPict] = useState("")


    const useStyles = makeStyles((theme) => ({
        margin: {
            margin: theme.spacing(1),
            width: "100%"
        },
        }));

    const classes = useStyles();


    useEffect(() => {
        getAllProductCat()
        .then(categoriesDB => {
            setCategories(categoriesDB)
        })
    }, [])


    const handleSubmit = () => {

        if(!productName) {
            setProductNameError(true)
            setProductNameErrorMsg("Veuillez saisir une dénomination")
        }
        else{
            setProductNameError(false)
            setProductNameErrorMsg("")
        }

        if(!productPrice) {
            setProductPriceError(true)
            setProductPriceErrorMsg("Veuillez saisir un prix")
        }
        else{
            setProductPriceError(false)
            setProductPriceErrorMsg("")
        }

        //S'il n'y a pas de fichier sélectionné
        if(!selectedFile){
            let datas = {
                name: productName,
                price: productPrice,
                description: productDesc,
                pict_url:""
            }
        }
        else {
        }
    }

    return(
        <div className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
            <div className="admin-container">
                    <AdminMenu />

                    <div className="admin-content">
                        <h4>Ajout d'un produit</h4>
                        <div className="product-add-container">
                            <form className="product-add-form">
                                <FormControl error className={classes.margin}>
                                    <TextField label="Nom" required onChange={(e) => {setProductName(e.target.value)}} error={productNameError}/>
                                    <FormHelperText>{productNameErrorMsg}</FormHelperText>
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Description" multiline rows={4} onChange={(e) => {setProductDesc(e.target.value)}} />
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Prix" type="number" onChange={(e) => {setProductPrice(e.target.value)}} InputProps={{
                                        endAdornment: <InputAdornment position="start">€</InputAdornment>,
                                    }} error={productPriceError}/>
                                    <FormHelperText>{productPriceErrorMsg}</FormHelperText>
                                </FormControl>

                                <FormControl className={classes.margin}>
                                    <InputLabel shrink={true}>Catégorie</InputLabel>
                                    <NativeSelect
                                        name="catégorie"
                                        onChange={(e) => {setSelectedCategory(e.target.value)
                                                         console.log(e.target.value)}}>
                                            <option value={0}>--</option>
                                            {categories.map((category) => {
                                                return(<option value={category.id} key={category.id}>{category.label}</option>)
                                            })}
                                    </NativeSelect>
                                </FormControl>

                                <FormControl className={classes.margin}>
                                    <input 
                                    accept="images/*"
                                    style={{display: 'none'}}
                                    id="file-select"
                                    type="file"
                                    onChange={(e) => {setSelectedFile(e.target.files[0])}}
                                    />
                                    <label htmlFor="file-select">
                                        <Button color="primary" component="span" className={classes.margin}>Ajouter une photo</Button>
                                        {selectedFile && selectedFile.name}
                                    </label>
                                </FormControl>


                                <Button variant="contained" color="primary" className={classes.margin} onClick={(e) => {handleSubmit()}}>Enregistrer</Button>
                            </form>
                        </div>
                    </div>
                </div>
            <Footer />
        </div>

    )
}

export default AdminProductAdd
