import React, {useState, useEffect} from "react";
import {connect} from "react-redux"

import {getAllProducts} from "../../api/products"
import {Link, Redirect} from "react-router-dom"

import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

import AdminMenu from "../../components/adminMenu"
import DeletePopUp from "../../components/deletePopUp";

import editIcon from "../../assets/icons/edit-blue.svg"
import deleteIcon from "../../assets/icons/delete-blue.svg"

const AdminProducts = (props) => {
    const [headerTitle, setHeaderTitle] = useState("Administration")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Administration", link:"/admin"},
                                                                {value: "Produits", link:null}])

    const [range, setRange] = useState(5)
    const [startPoint, setStartPoint] = useState(0)
    const [selectedPage, setSelectedPage] = useState(1)

    const [allProducts, setAllProducts] = useState([])

    useEffect(() => {
        getAllProducts()
        .then(response => {
            setAllProducts(response)
        })

    }, [])

    // Affichage des produits
    const showProducts = () => {
        // Récupération des utilisateurs
        // Définition de la liste des items affichés
        let items = []
        // Paramètres de la boucle d'affichage
        let loopMin;
        let loopMax;

        // Définition des paramètres pour la boucle d'affichage
        if(allProducts.length - startPoint < range){
          loopMin = startPoint
          loopMax = allProducts.length  
        }
        else {
            loopMin = startPoint
            loopMax = startPoint + range
        }

        // Génération des items pour l'affichage
        for(let i = loopMin; i < loopMax; i++){
            console.log("i = ", i)
            items.push(
                /*<div className="users-list-item">
                    <div className="user-item-name">{allProducts[i].first_name} {allProducts[i].last_name}</div>
                    <div className="edit-delete-user-container">
                        <Link to={"/admin/users/edit/"+users[i].id}><img src={editIcon} alt="Edit icon" className="edit-delete-user"/></Link>
                        {<Link to={"/admin/users/delete/"+users[i].id}><img src={deleteIcon} alt="Delete icon" className="edit-delete-user"/></Link>}

                    </div>
                </div>*/

                <div className="product-list-item">

                </div>
            )
        }

        return(
            <div className="product-list">
                {items}
            </div>

        )
    }

    // Au changement de page
    const changePage = (pageNumber) => {
        setPage(pageNumber)
        if(pageNumber > 1){
            setStartPoint(((pageNumber-1)*range))
        }
        else {
            setStartPoint(0) 
        }
    }

    // Affichage de la liste des pages
    const showPagesList = () => {
        let pagesList = []
        let users = props.users.users
        let numberOfPages = users.length / range


        for(let i = 1; i < numberOfPages+1; i ++) {
            if(i === selectedPage) {
                pagesList.push(
                    <div className="product-page-number-selected" onClick={(e) => {changePage(i)}}>
                        <p>{i}</p>
                    </div>
                )
            }
            else {
                pagesList.push(
                    <div className="product-page-number" onClick={(e) => {changePage(i)}}>
                        <p>{i}</p>
                    </div>
                )
            }

        }

        return( <div className="admin-user-page-selection">
            {pagesList}
        </div>) 
    }

    return(
        <div className="root">
                <Header />
                <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
                <div className="admin-container">
                    <AdminMenu />

                    <div className="admin-content">
                        <h4>Gestion des produits</h4>
                        <div className="product-list-container">
                            {showProducts()}
                        </div>
                        <div className="admin-product-page-selection-container">
                            {showPagesList()}
                        </div>
                    </div>
                </div>
                <Footer />
        </div>
    )
}

const mapStateToProps = (store) => {
    return {

    }
}   

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(AdminProducts)
