import React, {useState, useEffect} from "react"
import {getOneUser} from "../../api/user"

import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

import AdminMenu from "../../components/adminMenu"

import contactIcon from "../../assets/icons/email-blue.svg"

import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';

const AdminUserEdit = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Administration")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value:"Administration", link:"/admin"},
                                                                {value: "Utilisateurs", link:"/admin/users"},
                                                                {value: "Edition", link:null}])

    const [firstName, setFirstName] = useState("")
    const [firstNameError, setFirstNameError] = useState(false)
    const [firstNameErrorText, setFirstNameErrorText] = useState("")

    const [lastName, setLastName] = useState("")
    const [lastNameError, setLastNameError] = useState(false)
    const [lastNameErrorText, setLastNameErrorText] = useState("")

    const [email, setEmail] = useState("")
    const [emailError, setEmailError] = useState("")
    const [emailErrorText, setEmailErrorText] = useState("")

    const [password, setPassword] = useState("")
    const [passwordError, setPasswordError] = useState(false)
    const [passwordErrorText, setPasswordErrorText] = useState("")

    const [phone, setPhone] = useState("")
    const [phoneError, setPhoneError] = useState(false)
    const [phoneErrorText, setPhoneErrorText] = useState("")

    const [address, setAddress] = useState("")
    const [zip, setZip] = useState("")
    const [city, setCity] = useState("")

    // RegEx de vérification du mail
    let emailRegEx = /^[a-z]+([\.\-_]?[a-z0-9]+)*@([a-z]{3,})+(\.[a-z]{2,3})+$/

    // RegEx de vérification du Téléphone
    let phoneRegEx = /^(?:(?:\+|00)33|0)\s*[1-9](?:[\s.-]*\d{2}){4}$/

    useEffect(() => {
        getOneUser(props.match.params.id)
        .then((user) => {console.log(user)})
    }, [])

    const globalVerify = () => {

        if(!firstName){
            setFirstNameError(true)
            setFirstNameErrorText("Prénom non renseigné")
        }
        else {
            setFirstNameError(false)
            setFirstNameErrorText("")
        }

        if(!lastName){
            setLastNameError(true)
            setLastNameErrorText("Nom non renseigné")
        }
        else {
            setLastNameError(false)
            setLastNameErrorText("")
        }
        if(!email){
            setEmailError(true)
            setEmailErrorText("Adresse email non renseignée")
        }
        else {
            if(!email.match(emailRegEx)){
                setEmailError(true)
                setEmailErrorText("Adresse email non valide")
            }
            else {
                setEmailError(false)
                setEmailErrorText("")
            }
        }
        if(!password){
            setPasswordError(true)
            setPasswordErrorText("Mot de passe non reseigné")
        }
        else {
            setPasswordError(false)
            setPasswordErrorText("")
        }
        if(!phone){
            setPhoneError(true)
            setPhoneErrorText("N° de téléphone non renseigné")
        }
        else{
            if(!phone.match(phoneRegEx)){
                setPhoneError(true)
                setPhoneErrorText("N° de téléphone incorrect")
            }
            else {
                setPhoneError(false)
                setPhoneErrorText("")
            }
        }

        //Si tous les champs sont bons, on enregistre l'utilisateur
        if(firstName && lastName && email && password && phone && phone.match(phoneRegEx) && email.match(emailRegEx)){

            let datas = {
                firstName: firstName,
                lastName: lastName,
                email: email,
                password: password,
                phone: phone,
                address: address,
                zip: zip,
                city: city
            }    

            /*saveUser(datas)
            .then(response => {
                if(response.data.status === 401){
                    setEmailErrorText("Utilisateur existant")
                    setEmailError(true)
                }
                else{

                }
            })*/

        }

    }

    return(
        <div className="root">
                <Header />
                <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
                <div className="admin-container">
                    <AdminMenu />

                    <div className="admin-content">
                        <h4>Edition d'un utilisateur</h4>
                        <div className="user-edit-container">
                            <form className="" autoComplete="off">
                                <div className="user-edit-informations">
                                    <div className="user-edit-header">
                                        <img src={contactIcon} alt="Personnal information icon" />
                                        <p>Informations personnelles</p>
                                    </div>
                                    <div className="user-edit-informations-content">
                                        <FormControl error>
                                            <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={lastNameError} />
                                            <FormHelperText>{lastNameErrorText}</FormHelperText>
                                        </FormControl>

                                        <FormControl error>
                                            <TextField label="Prénom" color="primary" className="register-form-input" required onChange={(e) => {setFirstName(e.target.value)}} error={firstNameError} value={firstName}/>
                                            <FormHelperText>{firstNameErrorText}</FormHelperText>
                                        </FormControl>

                                        <FormControl error>
                                            <TextField label="Mot de passe" color="primary" className="register-form-input" required onChange={(e) => {setPassword(e.target.value)}} error={passwordError} type="password" value={password}/>
                                            <FormHelperText>{passwordErrorText}</FormHelperText>
                                        </FormControl>
                                    </div>
                                </div>
                                <div className="user-edit-contact">
                                        <div className="user-edit-header">
                                            <img src={contactIcon} alt="Contact icon" />
                                            <p>Contact</p>
                                        </div>
                                        <div className="user-edit-contact-content">

                                                <FormControl error>
                                                    <TextField label="Email" color="primary" className="edit-contact-form-input" required onChange={(e) => {setEmail(e.target.value)}} error={emailError} type="email" value={email}/>
                                                    <FormHelperText>{emailErrorText}</FormHelperText>
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Téléphone" color="primary" className="edit-contact-form-input" required  error={phoneError} type="tel"  onChange={(e) => {setPhone(e.target.value)}} />
                                                    <FormHelperText>{phoneErrorText}</FormHelperText>
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Adresse" color="primary" className="edit-contact-form-input" onChange={(e) => {setAddress(e.target.value)}} />
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Code Postal" color="primary" className="edit-contact-form-input" onChange={(e) => {setZip(e.target.value)}} />
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Ville" color="primary" className="edit-contact-form-input" onChange={(e) => {setCity(e.target.value)}} />
                                                </FormControl>


                                        </div>
                                        <Button className="admin-user-edit-button" color="primary" variant="contained">
                                                    Envoyer
                                                </Button>
                                    </div>


                            </form>
                        </div>

                    </div>
                </div>
                <Footer />
        </div>
    )
}

export default AdminUserEdit
