import React, {useState, useEffect} from "react"

import {config} from "../config"
import {getOneProduct} from "../api/products"

//Imports des composants de l'UI externes
import Header from "./headers/header"
import HeaderPages from "./headers/headerPages"
import Footer from "./footer"

//Imports depuis la librairie MaterialUI
import TextField from '@material-ui/core/TextField'
import { makeStyles } from "@material-ui/styles"
import { Button } from '@material-ui/core'
import InputAdornment from '@material-ui/core/InputAdornment';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart'


const Product = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Produit")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Boutique", link:"/shop"}])

    const [product, setProduct] = useState({})
    const [amount, setAmount] = useState("")

    //Définition des styles MaterialUI
    const useStyles = makeStyles((theme) => ({
        margin:{
            margin: theme.spacing(1),
        },
        qtyInput:{
            width:"100%"
        },
        addToBasket:{
            marginTop: theme.spacing(2),
            width:"100%"
        }
        }));

    const classes = useStyles();


    useEffect(() => {
        getOneProduct(props.match.params.id)
        .then(productDB => {
            setProduct(productDB)
            setHeaderTitle(productDB.name)
            setHeaderBreadcrumbs([...headerBreadcrumbs,
                                 {value: productDB.category, link:null},
                                 {value: productDB.name, link:null}])
        })
    }, [])

    const displayAmount = (e) => {
        setAmount((e.target.value*product.price).toFixed(2)+" €")
    }

    return(
        <section className="root">
            <Header />
            <section className="product-container">
                <section className="product-content">
                    <section className="product-presentation">
                        <section className="product-pict">
                            <img src={config.product_pict_url + product.pict_url} alt={product.name} />
                        </section>
                        <section className="product-infos">
                            <section className="product-price">
                                <p>Prix : <strong>{product.price} €/Kg</strong></p>
                            </section>
                            <section className="product-add-to-basket">
                                <TextField variant="filled" 
                                           label="Quantité désirée"
                                           type="number"
                                           InputProps={{
                                            endAdornment: <InputAdornment position="start">g</InputAdornment>,
                                        }} 
                                           className={classes.qtyInput}
                                           onChange={(e) => {displayAmount(e)}}/>
                                <p>Montant désiré : {amount}</p>
                                <Button variant="contained" color="primary" className={classes.addToBasket}><AddShoppingCartIcon /> Ajouter au panier</Button>
                            </section>
                        </section>
                        {product.description && <section className="product-description">
                            <p>Description</p>
                            <p>{product.description}</p>
                        </section>}
                    </section>

                </section>

            </section>
            <Footer />
        </section>
    )
}

export default Product
