import React, {useState, useEffect} from "react"

import {getOneRequest} from "../../api/requests"

//Imports des composants de l'UI externes
import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"
import AdminMenu from "../../components/adminMenu"

const AdminRequestEdit = (props) => {

    const headerTitle ="Administration"
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},{value: "Administration", link:"/admin"},{value: "Commandes", link:"/admin/requests"}])
    const [request, setRequest] = useState({})


    //Au chargement de la page
    useEffect(() => {
        getOneRequest(props.match.params.id)
        .then(requestDB => {
            setHeaderBreadcrumbs(headerBreadcrumbs => [...headerBreadcrumbs, {value:requestDB.request_number, link:null}])
            setRequest(requestDB)
        })

    }, [])

    return (
        <section className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
            <section className="admin-container">
                <AdminMenu />

                <section className="admin-content">
                    <h4>Commande nÂ°</h4>

                </section>
            </section>
            <Footer />
        </section>
    )
}

export default AdminRequestEdit
