import React, {useState, useEffect} from "react"
import {getOneUser, updateUser, updatePassword, updateRole, getAllRoles} from "../../api/user"

import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

import AdminMenu from "../../components/adminMenu"

import contactIcon from "../../assets/icons/email-blue.svg"

import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import NativeSelect from '@material-ui/core/NativeSelect';

const AdminUserEdit = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Administration")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value:"Administration", link:"/admin"},
                                                                {value: "Utilisateurs", link:"/admin/users"},
                                                                {value: "Edition", link:null}])

    const [firstName, setFirstName] = useState("")
    const [firstNameError, setFirstNameError] = useState(false)
    const [firstNameErrorText, setFirstNameErrorText] = useState("")


    const [lastName, setLastName] = useState("")
    const [lastNameError, setLastNameError] = useState(false)
    const [lastNameErrorText, setLastNameErrorText] = useState("")

    const [email, setEmail] = useState("")
    const [emailError, setEmailError] = useState("")
    const [emailErrorText, setEmailErrorText] = useState("")

    const [password, setPassword] = useState("")
    const [passwordUpdated, setPasswordUpdated] = useState(false)

    const [phone, setPhone] = useState("")
    const [phoneError, setPhoneError] = useState(false)
    const [phoneErrorText, setPhoneErrorText] = useState("")

    const [address, setAddress] = useState("")
    const [zip, setZip] = useState("")
    const [city, setCity] = useState("")

    const [idSelectedRole, setIdSelectedRole] = useState("")
    const [selectedRole, setSelectedRole] = useState("")

    const [roles, setRoles] = useState([])
    const [newRole, setNewRole] = useState(0)

    // RegEx de vérification du mail
    let emailRegEx = /^[a-z]+([\.\-_]?[a-z0-9]+)*@([a-z]{3,})+(\.[a-z]{2,3})+$/

    // RegEx de vérification du Téléphone
    let phoneRegEx = /^(?:(?:\+|00)33|0)\s*[1-9](?:[\s.-]*\d{2}){4}$/

    useEffect(() => {
        getOneUser(props.match.params.id)
        .then((user) => {
            setFirstName(user.first_name)
            setLastName(user.last_name)
            setEmail(user.email)
            setPhone(user.phone)
            setAddress(user.address)
            setZip(user.zip)
            setCity(user.city)
            setIdSelectedRole(user.id_role)
            setSelectedRole(user.role)
            setNewRole(user.id_role)
        })
        getAllRoles()
        .then(rolesDB => {
            setRoles(rolesDB.data.result)
        })
    }, [])

    const globalVerify = () => {

        if(!firstName){
            setFirstNameError(true)
            setFirstNameErrorText("Prénom non renseigné")
        }
        else {
            setFirstNameError(false)
            setFirstNameErrorText("")
        }

        if(!lastName){
            setLastNameError(true)
            setLastNameErrorText("Nom non renseigné")
        }
        else {
            setLastNameError(false)
            setLastNameErrorText("")
        }
        if(!email){
            setEmailError(true)
            setEmailErrorText("Adresse email non renseignée")
        }
        else {
            if(!email.match(emailRegEx)){
                setEmailError(true)
                setEmailErrorText("Adresse email non valide")
            }
            else {
                setEmailError(false)
                setEmailErrorText("")
            }
        }
        if(password !== ""){
            setPasswordUpdated(true)
        }
        else {
            setPasswordUpdated(false)
        }
        if(!phone){
            setPhoneError(true)
            setPhoneErrorText("N° de téléphone non renseigné")
        }
        else{
            if(!phone.match(phoneRegEx)){
                setPhoneError(true)
                setPhoneErrorText("N° de téléphone incorrect")
            }
            else {
                setPhoneError(false)
                setPhoneErrorText("")
            }
        }

        //Si tous les champs sont bons, on enregistre l'utilisateur
        if(firstName && lastName && email && phone && phone.match(phoneRegEx) && email.match(emailRegEx)){

            let datas = {
                first_name: firstName,
                last_name: lastName,
                email: email,
                phone: phone,
                address: address,
                zip: zip,
                city: city
            }    

            updateUser(datas, props.match.params.id)
            .then(responseUpdateUser => {
                if(responseUpdateUser.data.status === 200){
                    console.log("Succès de la mise à jour utilisateur")
                    if(passwordUpdated){
                        let datasPassword = {
                            password: password
                        }
                        updatePassword(datasPassword, props.match.params.id)
                        .then(responsePassword => { 
                            if(responsePassword.data.status === 200){
                                console.log("Succès de la mise à jour du mot de passe")
                            }
                            else {
                                console.log("Erreur lors de la mise à jour du mot de passe")
                            }
                        })
                    }
                }
                else {
                    console.log("Erreur lors de la mise à jour de l'utilisateur")
                }
            })
            /*saveUser(datas)
            .then(response => {
                if(response.data.status === 401){
                    setEmailErrorText("Utilisateur existant")
                    setEmailError(true)
                }
                else{

                }
            })*/

        }

    }

    const onSubmit = () => {
        globalVerify()
    }

    return(
        <div className="root">
                <Header />
                <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
                <div className="admin-container">
                    <AdminMenu />

                    <div className="admin-content">
                        <h4>Edition d'un utilisateur</h4>
                        <div className="user-edit-container">
                            <form autoComplete="off">
                                <div className="user-edit-informations">
                                    <div className="user-edit-header">
                                        <img src={contactIcon} alt="Personnal information icon" />
                                        <p className="admin-content-section-title">Informations personnelles</p>
                                    </div>
                                    <div className="user-edit-informations-content">
                                        <FormControl error>
                                            <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={lastNameError} value={lastName || ""} />
                                            <FormHelperText>{lastNameErrorText}</FormHelperText>
                                        </FormControl>

                                        <FormControl error>
                                            <TextField label="Prénom" color="primary" className="register-form-input" required onChange={(e) => {setFirstName(e.target.value)}} error={firstNameError} value={firstName || ""}/>
                                            <FormHelperText>{firstNameErrorText}</FormHelperText>
                                        </FormControl>

                                        <FormControl error>
                                            <TextField label="Mot de passe" color="primary" className="register-form-input" required onChange={(e) => {setPassword(e.target.value)
                                            e.target.value ? setPasswordUpdated(true) : setPasswordUpdated(false)}} type="password" defaultValue=""/>
                                        </FormControl>

                                        <FormControl>
                                            <InputLabel shrink={true}>Rôle</InputLabel>
                                            <NativeSelect
                                            name="role"
                                            className=""
                                            textFieldProps={{
                                                label: "Label",
                                                InputLabelProps: {shrink:true}
                                            }}>
                                                {roles.map((role) => {
                                                        return(<option value={role.id} key={role.id} selected={idSelectedRole === role.id ? true : false}>{role.label}</option>)
                                                })}
                                            </NativeSelect>
                                        </FormControl>
                                    </div>
                                </div>
                                <div className="user-edit-contact">
                                        <div className="user-edit-header">
                                            <img src={contactIcon} alt="Contact icon" />
                                            <p className="admin-content-section-title">Contact</p>
                                        </div>
                                        <div className="user-edit-contact-content">

                                                <FormControl error>
                                                    <TextField label="Email" color="primary" className="edit-contact-form-input" required onChange={(e) => {setEmail(e.target.value)}} error={emailError} type="email" value={email || ""}/>
                                                    <FormHelperText>{emailErrorText}</FormHelperText>
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Téléphone" color="primary" className="edit-contact-form-input" required  error={phoneError} type="tel" value={phone || ""
                                                    } onChange={(e) => {setPhone(e.target.value)}} />
                                                    <FormHelperText>{phoneErrorText}</FormHelperText>
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Adresse" color="primary" className="edit-contact-form-input" onChange={(e) => {setAddress(e.target.value)}} value={address || ""}/>
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Code Postal" color="primary" className="edit-contact-form-input" onChange={(e) => {setZip(e.target.value)}} value={zip || ""}/>
                                                </FormControl>

                                                <FormControl error>
                                                    <TextField label="Ville" color="primary" className="edit-contact-form-input" onChange={(e) => {setCity(e.target.value)}} value={city || ""}/>
                                                </FormControl>


                                        </div>
                                        <Button className="admin-user-edit-button" color="primary" variant="contained" onClick={onSubmit}>
                                                    Envoyer
                                                </Button>
                                </div>                          
                            </form>
                        </div>

                    </div>
                </div>
                <Footer />
        </div>
    )
}

export default AdminUserEdit
