import React, {useState, useEffect} from 'react'

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import { withStyles, makeStyles } from "@material-ui/styles";
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';

const RegisterPopUp = (props) => {

    const [firstName, setFirstName] = useState("")
    const [firstNameError, setFirstNameError] = useState(false)
    const [firstNameErrorText, setFirstNameErrorText] = useState("")

    const [lastName, setLastName] = useState("")
    const [lastNameError, setLastNameError] = useState(false)
    const [lastNameErrorText, setLastNameErrorText] = useState("")

    const [email, setEmail] = useState("")
    const [emailError, setEmailError] = useState("")
    const [emailErrorText, setEmailErrorText] = useState("")

    const [password, setPassword] = useState("")
    const [passwordError, setPasswordError] = useState(false)
    const [passwordErrorText, setPasswordErrorText] = useState("")


    const [phone, setPhone] = useState("")
    const [address, setAddress] = useState("")
    const [zip, setZip] = useState("")
    const [city, setCity] = useState("")
    const [redirect, setRedirect] = useState("")


    useEffect(() => {
        console.log(props.opened)
    }, [props])

    const onSubmit = () =>{

    }

    return(
        <Dialog
            open={props.opened}
            onClose={(e) => {props.onClickclose()}}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
        >
            <DialogTitle>S'enregistrer</DialogTitle>
            <DialogContent>
            <form className="register-form">
                <FormControl error>
                    <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={firstNameError} />
                    <FormHelperText>{firstNameErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={firstNameError} />
                    <FormHelperText>{firstNameErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={firstNameError} />
                    <FormHelperText>{firstNameErrorText}</FormHelperText>
                </FormControl>

                <FormControl error>
                    <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={firstNameError} />
                    <FormHelperText>{firstNameErrorText}</FormHelperText>
                </FormControl>
                <TextField label="Prénom" color="primary" className="register-form-input" required onChange={(e) => {setFirstName(e.target.value)}}/>
                <TextField label="Email" color="primary" className="register-form-input" type="email" required onChange={(e) => {setEmail(e.target.value)}}/>
                <TextField label="Mot de passe" color="primary" className="register-form-input" type="password" required onChange={(e) => {setPassword(e.target.value)}}/>
                <TextField label="Téléphone" color="primary" className="register-form-input" required onChange={(e) => {setPhone(e.target.value)}}/> 
                <TextField label="Adresse" color="primary" className="register-form-input" onChange={(e) => {setAddress(e.target.value)}}/> 
                <TextField label="Code Postal" color="primary" className="register-form-input" onChange={(e) => {setZip(e.target.value)}}/>
                <TextField label="Ville" color="primary" className="register-form-input" onChange={(e) => {setCity(e.target.value)}}/>
            </form>
            </DialogContent>
            <DialogActions>
            <Button onClick={(e) => {props.onClickclose()}} color="primary">
                Annuler
            </Button>
            <Button onClick={(e) => {onSubmit()}} color="primary" autoFocus>
                Envoyer
            </Button>
            </DialogActions>
        </Dialog>
    )
}

export default RegisterPopUp
