import axios from "axios"
import {config} from "../config"

export const saveUser = (datas) => {

    return axios.post(config.api_url + "/user/register", datas)
    .then(response => {
        return response
    })
    .catch(err => {
        return err
    })
}

export const loginUser = (datas) => {

    return axios.post(config.api_url + "/user/login", datas)
    .then(response => {
        return response
    })
    .catch(err => {
        return err
    })
}

export const getAllUsers = () => {

    return axios.get(config.api_url + "/user/all")
    .then(response => {
        return response
    })
    .catch(err => {
        console.log(err)
    })
} 

export const getOneUser = (id) => {

    return axios.get(config.api_url + "/users/"+id)
    .then(response => {
        return response.data.user[0]
    })
    .catch(err => console.log(err))
}

export const updateUser = (datas, id) => {
    return axios.put(config.api_url + "/users/update/"+id, datas)
    .then(response => {
        return response
    })
    .catch(err =>  console.log("err",err))
}

export const updatePassword = (datas, id) => {
    return axios.put(config.api_url + "/users/update/password/"+id, datas)
    .then(response => {
        return response
    })
    .catch(err => console.log(err))
}

export const updateRole = (datas, id) => {

    return axios.put(config.api_url + "/users/update/role/"+id, datas)
    .then(response => {
        return response
    })
    .catch(err => console.log(err))
}

export const getAllRoles = () => {
    return axios.get("users/roles/all")
}
