import React, {useState, useEffect} from "react"

import {config} from "../config"
import {getOneProduct} from "../api/products"

//Imports des composants de l'UI externes
import Header from "./headers/header"
import HeaderPages from "./headers/headerPages"
import Footer from "./footer"

const Product = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Produit")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Boutique", link:"/shop"}])

    const [product, setProduct] = useState({})

    useEffect(() => {
        getOneProduct(props.match.params.id)
        .then(productDB => {
            setProduct(productDB)
            setHeaderTitle(productDB.name)
            setHeaderBreadcrumbs([...headerBreadcrumbs,
                                 {value: productDB.category, link:null},
                                 {value: productDB.name, link:null}])
        })
    }, [])
    return(
        <section className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
            <section className="product-container">
                <section className="product-content">

                </section>

            </section>
            <Footer />
        </section>
    )
}

export default Product
