import React, {useState, useEffect} from "react"
import {connect} from "react-redux"

import {config} from "../config"

import {getAllProducts} from "../api/products"

//Imports des composants de l'UI externes
import Header from "./headers/header"
import HeaderPages from "./headers/headerPages"
import Footer from "./footer"

//Imports depuis la librairie MaterialUI
import TextField from '@material-ui/core/TextField';
import { makeStyles } from "@material-ui/styles";
import { Button } from '@material-ui/core';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputAdornment from '@material-ui/core/InputAdornment';
import SearchIcon from '@material-ui/icons/Search'

const Shop = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Boutique")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Boutique", link:null}])


    const [userSearchValue, setUserSearchValue] = useState("")

    const [products, setProducts] = useState([])
    //DÃ©finition des styles MaterialUI
    const useStyles = makeStyles((theme) => ({
        margin:{
            margin: theme.spacing(1),
        },
        inputTextWhite : {
            margin: theme.spacing(1),
            color:"white",
            backgroundColor:"white",
            borderTopLeftRadius:"4px",
            borderRadius:"4px",
        }
        }));

    const classes = useStyles();


    //A chargement de la page
    useEffect(() => {
        getAllProducts()
        .then(productsDB => {
            setProducts(productsDB)
        })
    }, [])


    return(
        <div className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
            <section className="shop-container">
                <aside>
                    <section className="aside-searchbox">
                        <FormControl className="footer-home-contact-formcontrol">
                            <TextField type="search"
                                        variant="filled" 
                                        className={classes.margin}
                                        label="Rechercher un produit"
                                        InputLabelProps={{style : {color : "#B4B4B4"}}}
                                        InputProps={{style : {color : "#12455A"}}} 
                                        value={userSearchValue}
                                        onChange={(e) => {setUserSearchValue(e.target.value)}}
                                        InputProps={{
                                            startAdornment: (
                                                <InputAdornment position="start">
                                                <SearchIcon />
                                                </InputAdornment>
                                            ),
                                            }}/>
                        </FormControl>
                    </section>
                    <section className="aside-basket">

                    </section>
                    <section className="aside-categories">

                    </section>
                </aside>
                <section className=""></section>
            </section>
            <Footer />
        </div>
    )
}

const mapStateToProps = (store) => {
    return {

    }
}

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(Shop)
