import {CONNECT_USER, LOGOUT_USER} from "./action-type"

export const connectUser = (user) => {
    return function(dispatch){
        console.log("connexion de l'utilisateur redux en cours")
        dispatch({
            type: CONNECT_USER,
            payload: user
        })
    }
}

export const logoutUser = () => {
    return function(dispatch){
        dispatch({
            type: LOGOUT_USER,
            payload: null
        })
    }
}
