import React, {useState, useEffect} from 'react';

import {config} from "../../config/config"
import {convertDate} from "../../utils/utils"

import {getDailyStand, getDailyStandDetails} from "../../api/dailyStand"

import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel"

//Imports des composants de l'UI externes
import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

const DailyStand = (props) => {

    const headerTitle = "Etal du jour"
    const headerBreadcrumbs = [{value: "Accueil", link:"/"},
                               {value: "Etail du jour", link:null}]
    const [dailyStand, setDailyStand] = useState({})
    const [dailyStandDetails, setDailyStandDetails] = useState([])

    //Au chargement de la page
    useEffect(() => {
        getDailyStand()
        .then(dailyStandDB => {
            setDailyStand(dailyStandDB)
            console.log(dailyStandDB)
            getDailyStandDetails()
            .then(dailyStandDetailsDB => {
                setDailyStandDetails(dailyStandDetailsDB)
            })
        })
    }, [])

    //Configuration du slider des avis
    const sliderSettings = {
        showStatus: false,
        showArrows: false,
        showThumbs:false,
        showIndicators: true,
        autoPlay:true,
        interval:5000,
        stopOnHover:true,
        infiniteLoop:true,
        swipeable:false,
    }

    return(
        <section className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>

            <section className="dailystand-container">
                <section className="dailystand-header">
                    {dailyStand ? <h4>Aujourd'hui, le {convertDate(dailyStand.creation_timestamp)}, nous vous proposons : </h4> : <h4>Pas d'Ã©tal du jour pour le moment. Veuillez revenir plus tard</h4>}
                </section>
                <section className="dailystand-content">
                <Carousel {...sliderSettings}>
                            {shopOpinions.map((opinion) => {
                                return (
                                    <article key={opinion.id} className="latest-shop-opinions-item-container">
                                        <section className="latest-shop-opinions-item">
                                            <section className="latest-shop-opinions-item-content">
                                                <div className="shop-opinions-item-left">
                                                    <p>"</p>
                                                </div>
                                                <div className="shop-opinions-item-comment">
                                                    <p>{opinion.comment}</p>
                                                    <p><em>- {opinion.first_name} {opinion.last_name}</em></p>
                                                </div>
                                                <div className="shop-opinions-item-right">
                                                    <p>"</p>
                                                </div>
                                            </section>
                                            {opinion.pict_url && 
                                                    <section className="shop-opinions-item-pict">
                                                        <img src={config.shop_opinion_pict_url + opinion.pict_url} alt={"illustration"+opinion.id} />
                                                    </section>}
                                        </section>
                                    </article>)
                            })}
                        </Carousel>
                </section>
            </section>
            <Footer />
        </section>
    )
}

export default DailyStand
