import React, {useState, useEffect} from "react"
import {Link} from "react-router-dom"

import {config} from "../config/config"
import {getBasket, deleteFromBasket} from "../utils/basket"
import {getOneProduct} from "../api/products"

//Imports des composants de l'UI externes
import Header from "./headers/header"
import HeaderPages from "./headers/headerPages"
import Footer from "./footer"

//Imports depuis la librairie MaterialUI
import TextField from '@material-ui/core/TextField'
import InputAdornment from '@material-ui/core/InputAdornment';
import { makeStyles } from "@material-ui/styles"
import { Button } from '@material-ui/core'
import DeleteIcon from '@material-ui/icons/Delete'

const Basket = (props) => {

    const headerTitle = "Panier"
    const headerBreadcrumbs = [{value: "Accueil", link:"/"}, {value: "Boutique", link:"/shop"}, {value: "Panier", link:null}]

    const [basket, setBasket] = useState([])
    const [displayedBasket, setDisplayedBasket] = useState([])

    let totalAmount = 0

    //Définition des styles MaterialUI
    const useStyles = makeStyles((theme) => ({
        qtyInput:{
            flex:"1"
        },
        addToBasket:{
            marginTop: theme.spacing(2),
            width:"100%"
        },
        deleteBtn:{
            color:"#cc0e00"
        }
        }));

    const classes = useStyles();

    //Au chargement de la page
    useEffect(() => {
        setBasket(getBasket())
    },[])

    const removeItemFromBasket = (id) => {
        deleteFromBasket(id)
        setBasket(getBasket())
    }

    const loadDisplayedBasket = () => {

        for(let i = 0; i < basket.length; i ++){
            let displayedBasketItem = <article></article>
        }


    }

    return(
        <section className="root">

            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>

            <section className="basket-container">
                <section className="basket-content-container">
                    <section className="basket-header">
                        <p>Mon panier</p>
                        <p>{basket && basket.length + " produit(s)"}</p>    
                    </section>
                    {!basket && 
                            <section className="empty-basket">
                                <p>Votre panier est vide</p>
                            <Button variant="contained" color="primary" component={Link} to ="/shop">Commencer mes achats</Button>
                            </section>}
                    {basket && 
                    <section className="basket-content">
                        {basket.map((basketItem, index) => {
                            let product = getOneProduct(basketItem.productId)

                                console.log(product)
                                if(product){
                                    let productAmount = (basketItem.quantity * product.price/1000).toFixed(2)
                                    totalAmount += parseFloat(productAmount)
                                    return(
                                        <article className="basket-item-container" key={index}>
                                            <section className="basket-item">
                                                <section className="basket-item-pict">
                                                    <img src={config.product_pict_url + product.pict_url} alt={"illustration"+basketItem.id}/>
                                                </section>
                                                <section className="basket-item-infos">
                                                    <Link className="general-link" to={"/shop/products/"+product.id}><p>{product.name}</p></Link>
                                                    <p><em>{product.category}</em></p>
                                                    {/*<p>{product.desc.substring(0,70) + "..."}</p>*/}
                                                    <p>{product.price} €/Kg TTC</p>

                                                </section>
                                            </section>
                                            <section className="basket-item-resume">
                                                <p>Quantité désirée : <span>{basketItem.quantity} g</span></p>
                                                <p>Prix total (TTC) : <span>{productAmount} € </span> </p>
                                            </section>
                                            <section className="basket-item-user-interactions">
                                                <Button className={classes.deleteBtn} onClick={(e) => {removeItemFromBasket(basketItem.product.id)}}><DeleteIcon /></Button>
                                            </section>
                                        </article>
                                    )
                                }



                        })}

                    </section>}
                </section>

                <aside className="basket-resume default-background-gradient">
                    <section className="basket-resume-header">
                        <p>Total</p>
                    </section>
                    <section className="basket-resume-content">
                        <article>
                            <p>Montant total (HT) : </p>
                            <p>{(totalAmount*0.8).toFixed(2)} €</p>
                        </article>
                        <article>
                            <p>Montant TVA : </p>
                            <p>{(totalAmount*0.2).toFixed(2)} €</p>
                        </article>
                        <article>
                            <p>Montant total (TTC) :</p>
                            <p><span>{totalAmount.toFixed(2)} €</span></p>
                        </article>
                    </section>

                    <section className="basket-resume-footer">
                        <Button variant="contained" color="primary">Commander</Button>
                    </section>
                </aside>
            </section>
            <Footer/>
        </section>
    )
}

export default Basket
