import {ALL_USERS, CONNECT_USER, LOGOUT_USER} from "./action-type"

export const connectUser = (user) => {
    return function(dispatch){
        dispatch({
            type: CONNECT_USER,
            payload: user
        })
    }
}

export const logoutUser = () => {
    return function(dispatch){
        dispatch({
            type: LOGOUT_USER,
            payload: null
        })
    }
}

export const getAllUsers = (users) => {
    return function(dispatch){
        dispatch({
            type: ALL_USERS,
            payload: users
        })
    }
}
