import React, {useState, useEffect} from "react";
import {connect} from "react-redux"
import {Link} from "react-router-dom"

import { Button } from '@material-ui/core';

import logo from "../../assets/images/logos/logo-complet.png"

import HeaderNav from "./headerNav"
import RegisterPopUp from "../../components/registerPopUp"
import LoginPopUp from "../../components/loginPopUp"

const HeaderHome = (props) => {

    const [openRegisterPopUp, setOpenRegisterPopUp] = useState(false);
    const [openLoginPopUp, setOpenLoginPopUp] = useState(false);
    const [isLogged, setIsLogged] = useState(false)

    const closeRegisterForm = () => {
        setOpenRegisterPopUp(false)
    }

    const closeLoginForm = () => {
        setOpenLoginPopUp(false)
    }

    const closeLoginFormLogged = () => {
        closeLoginForm()
        setIsLogged(true)
    }

    useEffect(() => {
        setIsLogged(props.user.isLogged)
    }, [props.user.isLogged])

    return (<div className="headerhome-container default-background-gradient">

                <HeaderNav isLogged={isLogged}/>

                {!isLogged && (<div className="login-register-home">
                    <Button color="secondary" variant="outlined" onClick={(e) => {setOpenLoginPopUp(true)}}>Se connecter</Button>
                    {openLoginPopUp && <LoginPopUp opened={openLoginPopUp} closeLoginForm={(e) => {closeLoginForm()}} closeLoginFormLogged={(e) =>{ closeLoginFormLogged()}}/>}

                    <Button color="secondary" variant="outlined" onClick={(e) => {setOpenRegisterPopUp(true)}}>S'enregistrer</Button>
                    {openRegisterPopUp && <RegisterPopUp opened={openRegisterPopUp} closeRegisterForm={(e) => {closeRegisterForm()}} />}
                </div>)}

                <div className="headerhome-content-container">
                    <div className="headerhome-content">
                        <img className="headerhome-logo" src={logo} alt="Logo"/>
                        <h1>Après la mer, <span>votre 
                            meilleure 
                            poissonnerie</span></h1>
                    </div>
                </div>
                <div className="header-buttons-container">
                    <Button color="primary" variant="contained">Etal du jour</Button>
                    {isLogged && <Button color="secondary" variant="outlined" component={Link} to="/shop">Boutique</Button>}

                </div>

            </div>)


}

const mapStateToProps = (store) => {
    return {
        user : store.user
    }
}

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(HeaderHome)
