import React, {useState, useEffect} from "react"
import {connect} from "react-redux"

import {loadAllRequests} from "../../actions/requests/requestAction"

//Imports des composants de l'UI externes
import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"
import AdminMenu from "../../components/adminMenu"

const AdminRequests = (props) => {

    const headerTitle ="Administration"
    const headerBreadcrumbs = [{value: "Accueil", link:"/"},{value: "Administration", link:"/admin"},{value: "Commandes", link:null}]

    const [displayedRequests, setDisplayedRequests] = useState([])

    //Au chargement de la page
    useEffect(() => {

    }, [])

    //A chaque modification du redux requests
    useEffect(() => {

    }, [props.requests])

    //Construction de la liste des commandes à afficher
    const loadDisplayedRequests = (requests) => {
        requests.map((requestItem) => {
            let requestDetails = []
            getAllRequestDetails(requestItem.id)
            .then((requestLines) => {
                requestLines.map((requestLineDetails) => {
                    requestDetails = [...requestDetails,
                        <article key={requestLineDetails.id} className="request-details-item">
                            <section className="request-details-item-pict">
                                <img src={config.product_pict_url + requestLineDetails.pict_url} alt={"illustration"+requestLineDetails.id}/>
                            </section>
                            <section className="request-details-item-infos">
                                <p>{requestLineDetails.name}</p>
                                <p>{requestLineDetails.quantity} g</p>
                                <p>{requestLineDetails.price} € TTC</p>
                            </section>
                        </article>]
                })

                setDisplayedRequests(displayedRequests => [...displayedRequests,
                    <article key={requestItem.id} className="profile-user-request-item">
                        <section className="request-item-header">
                            <p>N°{requestItem.request_number}</p>
                            <p>du {convertDate(requestItem.request_date)}</p>
                            <p>Statut : {requestItem.preparation_status}</p>
                        </section>
                        <section className="request-item-details">
                            {requestDetails}
                        </section>
                        <section className="request-item-resume">
                            <p>Total</p>
                            <p>{requestItem.total_amount} € TCC</p>
                        </section>
                    </article>])
            })



        })
    }  

    return (
        <div className="root">
                <Header />
                <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
                <section className="admin-container">
                    <AdminMenu />

                    <section className="admin-content">
                        <h4>Gestion des commandes</h4>

                    </section>
                </section>
                <Footer />
        </div>
    )
}

const mapStateToProps = (store) => {
    return {
        requests: store.requests
    }
}

const mapDispatchToProps = {
    loadAllRequests
}

export default connect(mapStateToProps, mapDispatchToProps)(AdminRequests)
