import React, {useState} from "react"
import {Link} from "react-router-dom"
import {connect} from"react-redux"

import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';


import menuIcon from "../../assets/icons/menu-icon.svg"
import adminIcon from "../../assets/icons/admin-icon.svg"
import profileIcon from "../../assets/icons/profile-icon.svg"
import cartIcon from "../../assets/icons/cart-icon.svg"
import searchIcon from "../../assets/icons/search-icon.svg"
import logoutIcon from "../../assets/icons/logout-icon.svg"

const HeaderNav = (props) => {

    const [drawerMenuOpened, setDrawerMenuOpened] = useState(false)


    return(<div className="header-nav-container">
                <img className="header-nav-menu-icon" src={menuIcon} alt="Icone menu" onClick={(e) => {setDrawerMenuOpened(true)}}/>
                {props.user.isLogged && (
                <nav className="header-nav">
                    {props.user.infos.role === "Admin" && <Link to="/admin"><img className="header-nav-icon" src={adminIcon} alt="Panneau d'administration"/></Link>}
                    <img className="header-nav-icon" src={profileIcon} alt="Profil"/>
                    <img className="header-nav-icon" src={cartIcon} alt="Panier"/>
                    <img className="header-nav-icon" src={searchIcon} alt="Rechercher"/>
                    <Link to="/user/logout"><img className="header-nav-icon" src={logoutIcon} alt="Se dÃ©connecter"/></Link>
                </nav>)}
                <Drawer anchor="left" open={props.opened} onClose={props.closeDrawerMenu()}>
        <List>
            <ListItem>
                <ListItemText primary="Accueil"/>
            </ListItem>
        </List>
    </Drawer>
            </div>)

}

const mapStateToProps = (store) => {
    return {
        user : store.user
    }
}

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(HeaderNav)
