import React, { useState, useEffect } from "react"
import {connect} from "react-redux"

import {updateUser, updatePassword} from "../../api/user"
import {config} from "../../config/config"

//Imports des composants de l'UI externes
import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

//Imports depuis la librairie MaterialUI
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import NativeSelect from '@material-ui/core/NativeSelect';
import {makeStyles } from "@material-ui/styles";

import contactIcon from "../../assets/icons/email-blue.svg"

const Profile = (props) => {

    const headerTitle = "Profil"
    const headerBreadcrumbs = [{value: "Accueil", link:"/"}, {value: "Profil", link:null}]


    const [firstName, setFirstName] = useState("")
    const [firstNameError, setFirstNameError] = useState(false)
    const [firstNameErrorText, setFirstNameErrorText] = useState("")


    const [lastName, setLastName] = useState("")
    const [lastNameError, setLastNameError] = useState(false)
    const [lastNameErrorText, setLastNameErrorText] = useState("")

    const [email, setEmail] = useState("")
    const [emailError, setEmailError] = useState("")
    const [emailErrorText, setEmailErrorText] = useState("")

    const [password, setPassword] = useState("")
    const [passwordUpdated, setPasswordUpdated] = useState(false)

    const [phone, setPhone] = useState("")
    const [phoneError, setPhoneError] = useState(false)
    const [phoneErrorText, setPhoneErrorText] = useState("")

    const [address, setAddress] = useState("")
    const [zip, setZip] = useState("")
    const [city, setCity] = useState("")

    //Définition des styles MaterialUI
    const useStyles = makeStyles((theme) => ({
        margin: {
            margin: theme.spacing(1),
        }
        }));

    const classes = useStyles();

    //Au chargement de la page
    useEffect(() => {
        setFirstName(props.user.infos.first_name)
        setLastName(props.user.infos.last_name)
    }, [])

    // Au click sur le bouton envoyer
    const onSubmit = () => {
        globalVerify()
    }

    const globalVerify = () => {

        if(!firstName){
            setFirstNameError(true)
            setFirstNameErrorText("Prénom non renseigné")
        }
        else {
            setFirstNameError(false)
            setFirstNameErrorText("")
        }

        if(!lastName){
            setLastNameError(true)
            setLastNameErrorText("Nom non renseigné")
        }
        else {
            setLastNameError(false)
            setLastNameErrorText("")
        }
        if(!email){
            setEmailError(true)
            setEmailErrorText("Adresse email non renseignée")
        }
        else {
            if(!email.match(config.emailRegEx)){
                setEmailError(true)
                setEmailErrorText("Adresse email non valide")
            }
            else {
                setEmailError(false)
                setEmailErrorText("")
            }
        }
        if(password !== ""){
            setPasswordUpdated(true)
        }
        else {
            setPasswordUpdated(false)
        }
        if(!phone){
            setPhoneError(true)
            setPhoneErrorText("N° de téléphone non renseigné")
        }
        else{
            if(!phone.match(config.phoneRegEx)){
                setPhoneError(true)
                setPhoneErrorText("N° de téléphone incorrect")
            }
            else {
                setPhoneError(false)
                setPhoneErrorText("")
            }
        }

        //Si tous les champs sont bons, on enregistre l'utilisateur
        if(firstName && lastName && email && phone && phone.match(config.phoneRegEx) && email.match(config.emailRegEx)){

            let datas = {
                first_name: firstName,
                last_name: lastName,
                email: email,
                phone: phone,
                address: address,
                zip: zip,
                city: city
            }    

            updateUser(datas, props.match.params.id)
            .then(responseUpdateUser => {
                if(responseUpdateUser.data.status === 200){
                    console.log("Succès de la mise à jour utilisateur")
                    if(passwordUpdated){
                        let datasPassword = {
                            password: password
                        }
                        updatePassword(datasPassword, props.match.params.id)
                        .then(responsePassword => { 
                            if(responsePassword.data.status === 200){
                                console.log("Succès de la mise à jour du mot de passe")
                            }
                            else {
                                console.log("Erreur lors de la mise à jour du mot de passe")
                            }
                        })
                    }
                }
                else {
                    console.log("Erreur lors de la mise à jour de l'utilisateur")
                }
            })            
        }

    }

    return(
        <section className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
            <section className="profile-container">
                <section className="">

                </section>
                <section className="user-edit-informations">
                    <section className="user-edit-header">
                        <img src={contactIcon} alt="Personnal information icon" />
                        <p className="admin-content-section-title">Informations personnelles</p>
                    </section>
                    <section className="user-edit-informations-content">
                        <FormControl error className={classes.margin}>
                            <TextField label="Nom" color="primary" className="register-form-input" required onChange={(e) => {setLastName(e.target.value)}} error={lastNameError} value={lastName || ""} />
                            <FormHelperText>{lastNameErrorText}</FormHelperText>
                        </FormControl>

                        <FormControl error className={classes.margin}>
                            <TextField label="Prénom" color="primary" className="register-form-input" required onChange={(e) => {setFirstName(e.target.value)}} error={firstNameError} value={firstName || ""}/>
                            <FormHelperText>{firstNameErrorText}</FormHelperText>
                        </FormControl>

                        <FormControl error className={classes.margin}>
                            <TextField label="Mot de passe" color="primary" className="register-form-input" onChange={(e) => {setPassword(e.target.value)
                            e.target.value ? setPasswordUpdated(true) : setPasswordUpdated(false)}} type="password" defaultValue=""/>
                        </FormControl>
                    </section>
                </section>
                <section className="user-edit-contact">
                        <section className="user-edit-header">
                            <img src={contactIcon} alt="Contact icon" />
                            <p className="admin-content-section-title">Contact</p>
                        </section>
                        <section className="user-edit-contact-content">

                                <FormControl error className={classes.margin} >
                                    <TextField label="Email" color="primary" required onChange={(e) => {setEmail(e.target.value)}} error={emailError} type="email" value={email || ""}/>
                                    <FormHelperText>{emailErrorText}</FormHelperText>
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Téléphone" color="primary" required  error={phoneError} type="tel" value={phone || ""
                                    } onChange={(e) => {setPhone(e.target.value)}} />
                                    <FormHelperText>{phoneErrorText}</FormHelperText>
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Adresse" color="primary" onChange={(e) => {setAddress(e.target.value)}} value={address || ""}/>
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Code Postal" color="primary" onChange={(e) => {setZip(e.target.value)}} value={zip || ""}/>
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Ville" color="primary" onChange={(e) => {setCity(e.target.value)}} value={city || ""}/>
                                </FormControl>


                        </section>
                        <Button className="admin-user-edit-button" color="primary" variant="contained" onClick={onSubmit}>
                                    Envoyer
                                </Button>
                </section>            
            </section>
            <Footer />
        </section>
    )
}

const mapStateToProps = (store) => {
    return {

    }

}

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(Profile)
