import React, {useState, useEffect} from "react"
import emailjs from 'emailjs-com';
import { configEmailJS } from "../configEmailJS";
import Header from "./headers/header"
import HeaderPages from "./headers/headerPages"
import Footer from "./footer"

import TextField from '@material-ui/core/TextField';
import { withStyles, makeStyles } from "@material-ui/styles";
import { Button } from '@material-ui/core';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';

import nearMe from "../assets/icons/near-me-blue.svg"
import phone from "../assets/icons/call-blue.svg"
import email from "../assets/icons/email-blue.svg"
import schedule from "../assets/icons/schedule-blue.svg"

const Contact = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Contact")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Contact", link:null}])

    //Données du formulaire de contact
    const [name, setName] = useState("")
    const [nameError, setNameError] = useState(false)
    const [nameErrorMsg, setNameErrorMsg] = useState("")

    const [mailFrom, setMailFrom] = useState("")
    const [mailFromError, setMailFromError] = useState(false)
    const [mailFromErrorMsg, setMailFromErrorMsg] = useState("")

    const [mailMsg, setMailMsg] = useState("")
    const [mailMsgError, setMailMsgError] = useState(false)
    const [mailMsgErrorMsg, setMailMsgErrorMsg] = useState("")

    const [snackBarOpened, setSnackBarOpened] = useState(false)

    const useStyles = makeStyles((theme) => ({
        inputTextWhite : {
            margin: theme.spacing(1),
            color:"white",
            '& .MuiInput-underline:before': {
            borderBottomColor: 'white',
            },
            '& .MuiInput-underline:hover:before': {
            borderBottomColor: 'white',

            },
            '& .MuiInput-underline:after': {
            borderBottomColor: 'white',
            color:"white"
            },
            width:"90%"
        }
        }));

    const classes = useStyles();

    useEffect(() => {
        emailjs.init(configEmailJS.userId)
    }, [])


    const sendMail = () => {
        emailjs.send(configEmailJS.serviceId, configEmailJS.templateId, {from_name:mailFrom, message: mailMsg})
    }

    return(<div className="root">
                <Header />
                <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>

                <section className="contact-infos-container">
                    <article className="contact-infos-item">
                        <div className="contact-infos-item-header">
                            <img src={nearMe} />
                            <p>Adresse</p>
                        </div>
                        <p>
                            <span>50 Avenue de Tarbes</span>
                            <span>65500 Vic-en-Bigorre</span>
                        </p>
                    </article>

                    <article className="contact-infos-item">
                        <div className="contact-infos-item-header">
                            <img src={phone} />
                            <p>Téléphone</p>
                        </div>
                        <p>05 62 31 94 33</p>
                    </article>

                    <article className="contact-infos-item">
                        <div className="contact-infos-item-header">
                            <img src={email} />
                            <p>Mail</p>
                        </div>
                        <p>mailtest@gmail.com</p>
                    </article>

                    <article className="contact-infos-item">
                        <div className="contact-infos-item-header">
                            <img src={schedule} />
                            <p>Horaires</p>
                        </div>
                        <div>
                            <p><strong>Mardi:</strong> 8h30 - 12h30 et 16h - 19h</p>
                            <p><strong>Mecredi:</strong> 8h30 - 13h</p>
                            <p><strong>Du jeudi au samedi:</strong> 8h30 - 12h30 et 16h - 19h</p>
                        </div>
                    </article>
                </section>
                {/* Intégration de la carte GMaps*/}
                <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2900.092654233334!2d0.05941222992945523!3d43.37508666409139!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12a9d973b17561ff%3A0x76550f41d8810aca!2s50%20Avenue%20de%20Tarbes%2C%2065500%20Vic-en-Bigorre!5e0!3m2!1sfr!2sfr!4v1626874184166!5m2!1sfr!2sfr" loading="lazy"></iframe>

                <div className="contact-form-container default-background-gradient">
                    <h4>Contactez-nous !</h4>
                    <p>Vous avez une question ? Une demande particulière ?</p>
                    <form>
                        <FormControl>

                        </FormControl>
                        <TextField
                                    className={classes.inputTextWhite}
                                    label="Nom"
                                    required
                                    InputLabelProps={{style : {color : "#B4B4B4"}}}
                                    InputProps={{style : {color : "#fff"}}}
                                    onChange={(e) => {setMailFrom(e.target.value)}}
                                />
                        <TextField
                                    className={classes.inputTextWhite}
                                    label="Mail"
                                    required
                                    InputLabelProps={{style : {color : "#B4B4B4"}}}
                                    InputProps={{style : {color : "#fff"}}}
                                />
                        <TextField
                                    className={classes.inputTextWhite}
                                    label="Message"
                                    required
                                    InputLabelProps={{style : {color : "#B4B4B4"}}}
                                    InputProps={{style : {color : "#fff"}}}
                                    multiline rows={4}
                                    onChange={(e) => {setMailMsg(e.target.value)}}
                                />  

                        <Button className={classes.margin} color="primary" variant="contained" onClick={(e) => {sendMail()}}>Envoyer</Button>
                    </form>

                </div>
                <Footer />
           </div>)
}

export default Contact
