import React, {useState, useEffect} from "react"
import {Link} from "react-router-dom"

import {config} from "../config/config"
import {getBasket} from "../utils/basket"

//Imports des composants de l'UI externes
import Header from "./headers/header"
import HeaderPages from "./headers/headerPages"
import Footer from "./footer"

//Imports depuis la librairie MaterialUI
import TextField from '@material-ui/core/TextField'
import InputAdornment from '@material-ui/core/InputAdornment';
import { makeStyles } from "@material-ui/styles"
import { Button } from '@material-ui/core'

const Basket = (props) => {

    const headerTitle = "Panier"
    const headerBreadcrumbs = [{value: "Accueil", link:"/"}, {value: "Boutique", link:"/shop"}, {value: "Panier", link:null}]

    const [basket, setBasket] = useState([])

    //Définition des styles MaterialUI
    const useStyles = makeStyles((theme) => ({
        qtyInput:{
            width:"90%"
        },
        addToBasket:{
            marginTop: theme.spacing(2),
            width:"100%"
        }
        }));

    const classes = useStyles();

    //Au chargement de la page
    useEffect(() => {
        setBasket(getBasket())
    },[])

    return(
        <section className="root">

            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>

            <section className="basket-container">
                <section className="basket-content-container">
                    <section className="basket-header">
                        <p>Mon panier</p>
                    </section>
                    {!basket && 
                            <section className="empty-basket">
                                <p>Votre panier est vide</p>
                            <Button variant="contained" color="primary" component={Link} to ="/shop">Commencer mes achats</Button>
                            </section>}
                    {basket && 
                    <section className="basket-content">
                        {basket.map((basketItem, index) => {
                            return(
                                <article className="basket-item-container" key={index}>
                                    <section className="basket-item">
                                        <section className="basket-item-pict">
                                            <img src={config.product_pict_url + basketItem.product.pict_url} alt={"illustration"+basketItem.product.id}/>
                                        </section>
                                        <section className="basket-item-infos">
                                            <Link className="general-link" to={"/shop/products/"+basketItem.product.id}><p>{basketItem.product.name}</p></Link>
                                            <p>{basketItem.product.category}</p>
                                            <p>{basketItem.product.description.substring(0,70) + "..."}</p>
                                        </section>
                                    </section>
                                    <section className="basket-item-user-interactions">
                                        <TextField variant="filled" 
                                            label="Quantité désirée"
                                            type="number"
                                            InputProps={{
                                                endAdornment: <InputAdornment position="start">g</InputAdornment>,
                                            }} 
                                            className={classes.qtyInput}
                                            value={basketItem.quantity}/>
                                    </section>
                                </article>
                            )
                        })}

                    </section>}
                </section>

                <aside>

                </aside>
            </section>
            <Footer/>
        </section>
    )
}

export default Basket
