                                        {/*<TextField variant="filled" 
                                            label="QuantitÃ© dÃ©sirÃ©e"
                                            type="number"
                                            InputProps={{
                                                endAdornment: <InputAdornment position="start">g</InputAdornment>,
                                            }} 
                                            className={classes.qtyInput}
                                            value={basketItem.quantity}/>*/}


                                        <Button className={classes.deleteBtn}><DeleteIcon /></Button>
                                    </section>
                                </article>
                            )
                        })}

                    </section>}
                </section>

                <aside className="basket-resume">
                    <p>Total</p>
                    <section>
                        <p>Montant total (HT) :</p>
                        <p></p>
                    </section>
                    <section>
                        <p>Montant TVA :</p>
                        <p></p>
                    </section>
                    <section>
                        <p>Montant total (TTC) :</p>
                        <p></p>
                    </section>
                </aside>
            </section>
            <Footer/>
        </section>
    )
}

export default Basket
