import React, {useState, useEffect} from "react"
import {connect} from "react-redux"

import {config} from "../config"

import Header from "./headers/header"
import HeaderPages from "./headers/headerPages"
import Footer from "./footer"

//Imports depuis la librairie MaterialUI
import TextField from '@material-ui/core/TextField';
import { makeStyles } from "@material-ui/styles";
import { Button } from '@material-ui/core';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputAdornment from '@material-ui/core/InputAdornment';
import SearchIcon from '@material-ui/icons/Search'

// Import des images
import wave1 from "../assets/images/wave1.svg"
import wave2 from "../assets/images/wave2.svg"

const Shop = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Boutique")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Boutique", link:null}])


    const [userSearchValue, setUserSearchValue] = useState("")

    //Définition des styles MaterialUI
    const useStyles = makeStyles((theme) => ({
        margin:{
            margin: theme.spacing(1),
        },
        inputTextWhite : {
            margin: theme.spacing(1),
            color:"white",
            backgroundColor:"white",
            borderTopLeftRadius:"4px",
            borderRadius:"4px",
        }
        }));

    const classes = useStyles();


    return(
        <div className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
            <div className="shop-container">
                <aside>
                    <div className="shop-searchbox">
                        <FormControl className="footer-home-contact-formcontrol">
                            <TextField type="search"
                                        variant="filled" 
                                        className={classes.margin}
                                        label="Rechercher un produit"
                                        InputLabelProps={{style : {color : "#B4B4B4"}}}
                                        InputProps={{style : {color : "#12455A"}}} 
                                        value={userSearchValue}
                                        onChange={(e) => {setUserSearchValue(e.target.value)}}
                                        InputProps={{
                                            startAdornment: (
                                                <InputAdornment position="start">
                                                <SearchIcon />
                                                </InputAdornment>
                                            ),
                                            }}/>
                        </FormControl>
                    </div>
                    <div>

                    </div>
                </aside>
            </div>
            <Footer />
        </div>
    )
}

const mapStateToProps = (store) => {
    return {

    }
}

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(Shop)
