                            <FormHelperText>{lastNameErrorText}</FormHelperText>
                        </FormControl>

                        <FormControl error className={classes.margin}>
                            <TextField label="PrÃ©nom" color="primary" className="register-form-input" required onChange={(e) => {setFirstName(e.target.value)}} error={firstNameError} value={firstName || ""}/>
                            <FormHelperText>{firstNameErrorText}</FormHelperText>
                        </FormControl>

                        <FormControl error className={classes.margin}>
                            <TextField label="Mot de passe" color="primary" className="register-form-input" onChange={(e) => {setPassword(e.target.value)
                            e.target.value ? setPasswordUpdated(true) : setPasswordUpdated(false)}} type="password" defaultValue=""/>
                        </FormControl>

                        <FormControl className={classes.margin}>
                            <InputLabel shrink={true}>RÃ´le</InputLabel>
                            <NativeSelect
                            name="role"
                            className=""
                            textFieldProps={{
                                label: "Label",
                                InputLabelProps: {shrink:true}
                            }}
