import React, {useState} from 'react'
import {saveUser} from "../api/user"

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button } from '@material-ui/core';

import {deleteUser} from "../api/user"

const [redirect, setRedirect] = useState(false)

const deleteUserAction = (id) => {
    deleteUser(id)
}

const DeletePopUp = (props) => {

    return(
        <div>
        <Dialog
        open={props.opened}
        onClose={(e) => {props.closeDeleteForm()}}>
            <DialogTitle>Suppression</DialogTitle>
            <DialogContent>
                <p>Voulez vous r√©ellement supprimer l'utilisateur {props.userName}</p>

            </DialogContent>
            <DialogActions>
                <Button color="primary" onClick={(e) => {props.closeDeleteForm()}}>Annuler</Button>
                <Button color="primary" onClick={(e) => {deleteUserAction(props.userId)}}>Supprimer</Button>
            </DialogActions>
        </Dialog></div>
    )
}

export default DeletePopUp
