import React, {useState, useEffect} from "react"

import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"

import AdminMenu from "../../components/adminMenu"

import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputAdornment from '@material-ui/core/InputAdornment';
import {makeStyles } from "@material-ui/styles";

const AdminProductAdd = (props) => {

    const [headerTitle, setHeaderTitle] = useState("Administration")
    const [headerBreadcrumbs, setHeaderBreadcrumbs] = useState([{value: "Accueil", link:"/"},
                                                                {value: "Administration", link:"/admin"},
                                                                {value: "Produits", link:"/admin/products"},
                                                                {value: "Ajout", link:null}])

    const [productName, setProductName] = useState("")
    const [productNameError, setProductNameError] = useState(false)
    const [productNameErrorMsg, setProductNameErrorMsg] = useState("")

    const [productPrice, setProductPrice] = useState("")
    const [productPriceError, setProductPriceError] = useState(false)
    const [productPriceErrorMsg, setProductPriceErrorMsg] = useState("")

    const [productDesc, setProductDesc] = useState("")

    const [productPict, setProductPict] = useState("")


    const useStyles = makeStyles((theme) => ({
        margin: {
            margin: theme.spacing(1),
        },
        }));

    const classes = useStyles();

    return(
        <div className="root">
            <Header />
            <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
            <div className="admin-container">
                    <AdminMenu />

                    <div className="admin-content">
                        <h4>Ajout d'un produit</h4>
                        <div className="product-add-container">
                            <form className="product-add-form">
                                <FormControl error className={classes.margin}>
                                    <TextField label="Nom" required onChange={(e) => {setProductName(e.target.value)}} error={productNameError}/>
                                    <FormHelperText>{productNameErrorMsg}</FormHelperText>
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Description" multiline rows={4} onChange={(e) => {setProductDesc(e.target.value)}} />
                                </FormControl>

                                <FormControl error className={classes.margin}>
                                    <TextField label="Description" multiline rows={4} onChange={(e) => {setProductDesc(e.target.value)}} />
                                </FormControl>

                                <Button variant="contained" color="primary">Ajouter</Button>
                            </form>
                        </div>
                    </div>
                </div>
            <Footer />
        </div>

    )
}

export default AdminProductAdd
