import React, {useState} from 'react'
import {connect} from "react-redux"
import {saveUser} from "../api/user"
import {config} from "../config"

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import {makeStyles } from "@material-ui/styles";



const ShopOpinionPopUp = (props) => {

    //Définition des variables
    const [opinion, setOpinion] = useState("")
    const [opinionError, setOpinionError] = useState(false)
    const [opinionErrorMsg, setOpinionErrorMsg] = useState("")

    const [selectedFile, setSelectedFile] = useState("")
    const [filePreview, setFilePreview] = useState("")
    const [defaultPict, setDefaultPict] = useState("no-pict.png")

    //Définition des styles MaterialUI
    const useStyles = makeStyles((theme) => ({
        margin:{
            margin: theme.spacing(1),
        },
        inputTextWhite : {
            margin: theme.spacing(1),
            color:"white",
            backgroundColor:"white",
            borderTopLeftRadius:"4px",
            borderRadius:"4px",
        }
        }));

    const classes = useStyles();



    const [firstName, setFirstName] = useState("")
    const [firstNameError, setFirstNameError] = useState(false)
    const [firstNameErrorText, setFirstNameErrorText] = useState("")

    const [lastName, setLastName] = useState("")
    const [lastNameError, setLastNameError] = useState(false)
    const [lastNameErrorText, setLastNameErrorText] = useState("")

    const [email, setEmail] = useState("")
    const [emailError, setEmailError] = useState("")
    const [emailErrorText, setEmailErrorText] = useState("")

    const [password, setPassword] = useState("")
    const [passwordError, setPasswordError] = useState(false)
    const [passwordErrorText, setPasswordErrorText] = useState("")

    const [phone, setPhone] = useState("")
    const [phoneError, setPhoneError] = useState(false)
    const [phoneErrorText, setPhoneErrorText] = useState("")

    const [address, setAddress] = useState("")
    const [zip, setZip] = useState("")
    const [city, setCity] = useState("")

    const globalVerify = () => {

            if(!firstName){
                setFirstNameError(true)
                setFirstNameErrorText("Prénom non renseigné")
            }
            else {
                setFirstNameError(false)
                setFirstNameErrorText("")
            }

            if(!lastName){
                setLastNameError(true)
                setLastNameErrorText("Nom non renseigné")
            }
            else {
                setLastNameError(false)
                setLastNameErrorText("")
            }
            if(!email){
                setEmailError(true)
                setEmailErrorText("Adresse email non renseignée")
            }
            else {
                if(!email.match(config.emailRegEx)){
                    setEmailError(true)
                    setEmailErrorText("Format d'email non valide")
                }
                else {
                    setEmailError(false)
                    setEmailErrorText("")
                }
            }

            //Si tous les champs sont bons, on enregistre l'utilisateur
            if(firstName && lastName && email && password && phone && email.match(config.emailRegEx)){

                let datas = {
                    firstName: firstName,
                    lastName: lastName,
                    email: email,
                    password: password,
                    phone: phone,
                    address: address,
                    zip: zip,
                    city: city
                }    

                saveUser(datas)
                .then(response => {
                    if(response.data.status === 401){
                        setEmailErrorText("Utilisateur existant")
                        setEmailError(true)
                    }
                    else{
                        props.closeShopOpinionPopUp()
                    }
                })

            }

    }

    const onSubmit = () =>{
        globalVerify()

    }

    return(
        <Dialog
            open={props.opened}
            onClose={(e) => {props.closeShopOpinionPopUp()}}
        >
            <DialogTitle>Avis</DialogTitle>
            <DialogContent>
            <form className="shop">
                <FormControl error>
                <TextField variant="filled" 
                            className={classes.inputTextWhite}
                            label="Message"
                            required
                            multiline rows={4}
                            error={opinionErrorMsg}
                            onChange={(e) => {setOpinion(e.target.value)}}/>
                    <FormHelperText>{opinionErrorMsg}</FormHelperText>
                </FormControl>

                <FormControl className={classes.margin}>
                    <input 
                    accept="images/*"
                    style={{display: 'none'}}
                    id="file-select"
                    type="file"
                    onChange={(e) => {setSelectedFile(e.target.files[0])
                                        if(e.target.files.length > 0) {
                                        setFilePreview(URL.createObjectURL(e.target.files[0]))
                                        }
                                        else {
                                        setFilePreview("")
                                    }
                                        }}
                    />
                    <label htmlFor="file-select">
                        <Button color="primary" component="span" className={classes.margin}>Ajouter une photo</Button>
                        {selectedFile && selectedFile.name}
                    </label>

                </FormControl>
                <img className="product-add-file-preview" src={filePreview} />
            </form>
            </DialogContent>
            <DialogActions>
            <Button onClick={(e) => {props.closeShopOpinionPopUp()}} color="primary">
                Annuler
            </Button>
            <Button onClick={(e) => {onSubmit()}} color="primary">
                Envoyer
            </Button>
            </DialogActions>
        </Dialog>
    )
}

const mapStateToProps = (store) => {
    return {
        user: store.user
    }
}

const mapDispatchToProps = {

}

export default connect(mapStateToProps, mapDispatchToProps)(ShopOpinionPopUp)
