import React, {useState, useEffect} from "react";

//Imports des composants de l'UI externes
import Header from "../headers/header"
import HeaderPages from "../headers/headerPages"
import Footer from "../footer"
import AdminMenu from "../../components/adminMenu"

import {getDailyStand, getDailyStandDetails, saveDailyStand, saveDailyStandDetail, saveDailyStandDetailPict, deleteDailyStand, updateDailyStand, deleteDailyStandDetails} from "../../api/dailyStand"

//Imports de la librairie MaterialUI
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';

const AdminDailyStand = (props) => {

    const headerTitle = "Administration"
    const headerBreadcrumbs = [{value: "Accueil", link:"/"},{value: "Administration", link:"/admin"}, {value: "Etal", link:null}]

    const [dailyStand, setDailyStand] = useState({id:"", description:"", creation_timestamp:""})
    const [contentAdded, setContentAdded] = useState([])
    const [dailyStandItems, setDailyStandItems] = useState([])
    const [errorForm, setErrorForm] = useState(false)
    const [dailyStandItemsDisplayed, setDailyStandItemsDisplayed] = useState([])

    //Ajout des champs permettant de compléter l'étal du jour
    const addElement = () => {
        setDailyStandItems(dailyStandItems => [...dailyStandItems,
            {pict_url:"",
            description: "",
            filePreview: "",
            selectedFile: ""}])
    }

    //Au chargement de la page
    useEffect(() => {
        getDailyStand()
        .then(dailyStandDB => {
            if(dailyStandDB){
                setDailyStand(dailyStandDB)
                getDailyStandDetails()
                .then(dailyStandItemsDB)
            }
        })
    }, [])

    //A chaque modification de dailyStandItems
    useEffect(() => {
        if(dailyStandItems.length > 0){
            let index = contentAdded.length
            setContentAdded(contentAdded => [...contentAdded, 
                <article key={index}>
                    <section>
                        <FormControl>
                            <input 
                            accept="images/*"
                            style={{display: 'none'}}
                            id={"file-select"+index}
                            type="file"
                            onChange={(e) => {changePicture(index, e.target.files[0])}}
                            />
                            <label htmlFor={"file-select"+index}>
                                <Button color="primary" component="span">Ajouter une photo</Button>
                            </label>
                        </FormControl>
                        {dailyStandItems[index].selectedFile && <p>{dailyStandItems[index].selectedFile.name}</p>}
                    </section>
                    <section>
                        <FormControl>
                            <TextField label="Description" variant="filled" onChange={(e) => {changeDescription(contentAdded.length, e.target.value)}}/>
                        </FormControl>
                    </section>
                </article>
            ])
        }

    }, [dailyStandItems])

    const changeDescription = (index, value) => {
        dailyStandItems[index].description = value
    }

    const changePicture = (index, file) => {
        dailyStandItems[index].filePreview = URL.createObjectURL(file)
        dailyStandItems[index].selectedFile = file

    }

    //Initialisation d'un nouveau étal
    const newDailyStand = () => {
        deleteDailyStand()
        deleteDailyStandDetails()
        setDailyStand({id:"", description:"", creation_timestamp:""})
    }

    //Fonction d'enregistrement de l'étal
    const handleSave = () => {

        //Vérification des données avant enregistrement
        let conformFiles = false
        for(let i = 0; i < dailyStandItems.length; i++){
            if(dailyStandItems[i].selectedFile){
                conformFiles = true
            }
        }

        if(!conformFiles && !dailyStand.description){
            setErrorForm(true)
        } else {
            setErrorForm(false)
            let dailyStandDatas = {description: dailyStand.description}
            if(dailyStand.id === ""){
                saveDailyStand(dailyStandDatas)
                .then(responseDailyStand => {
                    setDailyStand({...dailyStand, ["id"]: responseDailyStand.data.result.insertId})
                    for(let i = 0; i < dailyStandItems.length; i++){
                        saveDailyStandDetailPict(dailyStandItems[i].selectedFile)
                        .then(responseDailyStandDetailPict => {
                            let dailyStandDetailDatas = {
                                description: dailyStandItems[i].description,
                                pict_url: responseDailyStandDetailPict.data.url || "",
                                id_daily_stand: responseDailyStand.data.result.insertId
                            }
                            saveDailyStandDetail(dailyStandDetailDatas)
                        })
                    }
                })
            }
            else {
                updateDailyStand(dailyStandDatas)
                .then(response => {
                    for(let i = 0; i < dailyStandItems.length; i++){
                        if(dailyStandItems[i].selectedFile){
                            saveDailyStandDetailPict(dailyStandItems[i].selectedFile)
                            .then(responseDailyStandDetailPict => {
                                let dailyStandDetailDatas = {
                                    description: dailyStandItems[i].description,
                                    pict_url: responseDailyStandDetailPict.data.url  || "",
                                    id_daily_stand: dailyStand.id
                                }
                                saveDailyStandDetail(dailyStandDetailDatas)
                            })
                        }
                    }
                })
            }
        }

    }

    return(
        <section className="root">
                <Header />
                <HeaderPages headerTitle={headerTitle} headerBreadcrumbs={headerBreadcrumbs}/>
                <section className="admin-container">
                    <AdminMenu />

                    <section className="admin-content">
                        <h4>Gestion de l'étal du jour</h4>
                        <section className="admin-dailystand-interractions">
                            {errorForm && <p>Veuillez saisir une description de l'étal et/ou au moins une image d'illustration</p>}
                            {dailyStand.id !== "" && <Button variant="contained" color="primary" onClick={(e) => {newDailyStand()}}>Nouvel Etal</Button>}
                            <Button variant="contained" color="primary" onClick={(e) => {addElement()}}>Ajouter un élément</Button>
                        </section>
                        <section className="admin-dailystand-content">
                            <FormControl>
                                <TextField label="Description de l'étal" multiline rows={8} variant="filled" 
                                onChange={(e) => {setDailyStand({...dailyStand, ["description"]: e.target.value})}}
                                value={dailyStand.description}/>
                            </FormControl>
                            {contentAdded}
                        </section>
                        <Button variant="contained" color="primary" onClick={(e) => handleSave()}>Enregistrer</Button>
                    </section>
                </section>
                <Footer />
        </section>
    )

}

export default AdminDailyStand
